mapper.select:

where 1 = 1

	and a.name like concat('%',#{name},'%')


	and a.status = #{status}


	and a.duty_id = #{duty.userId}

mapper.update:
update 
	task_project
set 
	name = #{name},
	remark = #{remark}
where id = #{id}

update 
	task_project
set 
	status = #{status}
